// Replace the "Base64 to Buffer" function (node 27626111ff3dda90)
// with this improved version:

// Store the original base64 for UI display later
msg.originalImage = msg.payload;

// Check if the payload is a proper base64 data URI
if (!msg.payload || typeof msg.payload !== 'string') {
    node.warn("Invalid camera data received");
    return null;
}

// Convert to buffer for API processing
try {
    let base64Data = msg.payload.replace(/^data:image\/\w+;base64,/, '');
    msg.payload = Buffer.from(base64Data, 'base64');

    // Verify that we have valid data
    if (msg.payload.length === 0) {
        node.warn("Empty image data received");
        return null;
    }

    return msg;
} catch (error) {
    node.error("Error processing image: " + error.message);
    return null;
}

let distance = parseFloat(msg.payload);
let occupied = distance < 20;

msg.payload = {
    distance: distance,
    sensorStatus: occupied ? "occupied" : "vacant"
};

// Store in flow context for fusion with camera data
flow.set('sensorStatus', msg.payload.sensorStatus);
flow.set('distance', distance);
flow.set('lastDistanceUpdate', new Date().getTime());

return msg;

// Get the status from the API response
let cameraStatus = msg.payload.status;

// Store in flow context for later use
flow.set('cameraStatus', cameraStatus);
flow.set('lastImage', msg.originalImage);
flow.set('lastCameraUpdate', new Date().getTime());

// Trigger the fusion node
msg.payload = {
    cameraStatus: cameraStatus,
    trigger: 'camera'
};

return msg;

// Prepare message to trigger fusion
msg.payload = {
    sensorStatus: msg.payload.sensorStatus,
    trigger: 'sensor'
};

return msg;

// Replace the "Enhanced Data Fusion" function (node dd6b604ad481614c)
// with this improved version:

// Get the latest status from both sensors from flow context
let cameraStatus = flow.get('cameraStatus') || 'unknown';
let sensorStatus = flow.get('sensorStatus') || 'unknown';
let distance = flow.get('distance') || 0;
let lastImage = flow.get('lastImage') || '';

// Get last update timestamps
let lastCameraUpdate = flow.get('lastCameraUpdate') || 0;
let lastDistanceUpdate = flow.get('lastDistanceUpdate') || 0;
let currentTime = new Date().getTime();

// Check if data is stale (more than 30 seconds old)
let cameraStale = (currentTime - lastCameraUpdate) > 30000;
let sensorStale = (currentTime - lastDistanceUpdate) > 30000;

// Mark status as unknown if data is stale
if (cameraStale) cameraStatus = 'unknown';
if (sensorStale) sensorStatus = 'unknown';

// Previous combined status
let previousStatus = flow.get('combinedStatus') || 'unknown';
let lastStatusChange = flow.get('lastStatusChange') || currentTime;

// Get parking statistics
let parkingCount = flow.get('parkingCount') || 0;
let totalTime = flow.get('totalParkingTime') || 0;

// Enhanced fusion logic - BOTH sensors must detect a car for 'occupied' status
let combinedStatus;
if (cameraStatus === 'unknown' || sensorStatus === 'unknown') {
    combinedStatus = 'checking';
} else if (cameraStatus === 'occupied' && sensorStatus === 'occupied') {
    combinedStatus = 'occupied';
} else {
    combinedStatus = 'vacant';
}

// Update statistics if status changed
if (combinedStatus !== previousStatus) {
    // Calculate time spent in previous state
    let timeInState = currentTime - lastStatusChange;

    // If changing from occupied to vacant, update statistics
    if (previousStatus === 'occupied' && combinedStatus === 'vacant') {
        parkingCount++;
        totalTime += timeInState;
        flow.set('parkingCount', parkingCount);
        flow.set('totalParkingTime', totalTime);

        // Log this parking event
        let parkingHistory = flow.get('parkingHistory') || [];
        parkingHistory.push({
            start: new Date(lastStatusChange).toISOString(),
            end: new Date().toISOString(),
            duration: Math.round(timeInState / 1000) // in seconds
        });

        // Keep only last 10 events
        if (parkingHistory.length > 10) {
            parkingHistory = parkingHistory.slice(-10);
        }
        flow.set('parkingHistory', parkingHistory);
    }

    // Update last status change time
    flow.set('lastStatusChange', currentTime);
}

// Store the new combined status
flow.set('combinedStatus', combinedStatus);

// Calculate average parking time
let avgTime = parkingCount > 0 ? Math.round(totalTime / parkingCount / 1000) : 0; // in seconds

// Always send update when there's new camera data to ensure UI updates
let shouldUpdate = combinedStatus !== previousStatus ||
    msg.payload.trigger === 'camera' ||
    msg.payload.trigger === 'health_check';

if (shouldUpdate) {
    // Create comprehensive payload with all data
    msg.payload = {
        image: lastImage,
        distance: distance,
        cameraStatus: cameraStatus,
        sensorStatus: sensorStatus,
        combinedStatus: combinedStatus,
        timestamp: new Date().toISOString(),
        parkingCount: parkingCount,
        averageParkingTime: avgTime,
        cameraStale: cameraStale,
        sensorStale: sensorStale,
        history: flow.get('parkingHistory') || [],
        lastStatusChange: lastStatusChange
    };
    return msg;
} else {
    return null; // No change, don't update UI
}

// Get last update timestamps
let lastCameraUpdate = flow.get('lastCameraUpdate') || 0;
let lastDistanceUpdate = flow.get('lastDistanceUpdate') || 0;
let currentTime = new Date().getTime();

// Check if data is stale (more than 30 seconds old)
let cameraStale = (currentTime - lastCameraUpdate) > 30000;
let sensorStale = (currentTime - lastDistanceUpdate) > 30000;

// Prepare status message
let msg = { payload: {} };

// If either sensor is stale, trigger the fusion to update the UI
if (cameraStale || sensorStale) {
    msg.payload = {
        trigger: 'health_check'
    };
    return msg;
}

return null;
